$scriptPath = Split-Path -Parent $PSScriptRoot; Set-Location -Path $scriptPath; $commitMessage = "Updated assets"; $gitUserName = git config user.name; if (-not $gitUserName) { Start-Process "https://github.com/login"; Write-Host "Auth required" -ForegroundColor Yellow; git config --global user.email "poojapitiyans@gmail.com"; git config --global user.name "Poojapitiyacc.lk"; return }; try { Write-Host "Uploading..."; git pull origin main -q | Out-Null; git add . | Out-Null; git commit -m $commitMessage -q | Out-Null; git push origin main -q | Out-Null; Write-Host "Success" -ForegroundColor Green } catch { Write-Host "Error: $_" -ForegroundColor Red }
